import { useEffect, useRef } from "react";
import "../styles/LandingPage.css";

export default function LandingPage(){
    const imageContainer = useRef(null);

    let initialPos, mousePos, maxPos;
    let percentage, lastPercentage = 0;
    let mouseDown = false;

    useEffect(() => {
        const savePos = (event) => {
            initialPos = event.clientX;
            
            mouseDown = true;
        };

        document.body.addEventListener("pointerdown", savePos);

        return () => {
            document.body.removeEventListener("pointerdown", savePos);
        };
    });

    useEffect(() => {
        const releasePosition = () => {
            lastPercentage = percentage;

            mouseDown = false;
        };

        document.body.addEventListener("pointerup", releasePosition);

        return () => {
            document.body.removeEventListener("pointerup", releasePosition);
        };
    });

    useEffect(() => {
        const moveContainer = (event) => {
            if(!mouseDown)
                return;
            
            mousePos = event.clientX - parseFloat(initialPos);
            maxPos = window.innerWidth / 2; 

            percentage = (mousePos / maxPos) * 100 + lastPercentage;

            if(percentage > 10)
                percentage = 10;
            else if(percentage < -40)
                percentage = -40;

            if(imageContainer.current){
                imageContainer.current.animate({
                    transform: `translate(${percentage}%, 0%)`
                }, {duration: 1200, fill: "forwards"});
            }

            const images = imageContainer.current.querySelectorAll("img");

            for(const image of images){
                console.log(image);

                if(image){
                    image.animate({
                        objectPosition: `${percentage + 50}% 50%`
                    }, {duration: 1200, fill: "forwards"});
                }
            }
        };

        document.body.addEventListener("pointermove", moveContainer);

        return () => {
            document.body.removeEventListener("pointermove", moveContainer);
        };
    });

    return(
        <>
            <div className="bodyContainer">
                <div ref={imageContainer} className="imagesContainer">
                    <img  src="data:image/jpeg;base64," alt="" draggable="false"/>
                    <img  src="data:image/jpeg;base64," alt="" draggable="false"/>
                    <img  src="data:image/jpeg;base64," alt="" draggable="false"/>
                    <img  src="data:image/jpeg;base64," alt="" draggable="false"/>
                    <img  src="data:image/jpeg;base64," alt="" draggable="false"/>
                    <img  src="data:image/jpeg;base64," alt="" draggable="false"/>
                    <img src="data:image/jpeg;base64," alt="" draggable="false"/>
                </div>
            </div>
        </>
    )
}